package org.example;

import org.openapitools.client.ApiClient;
import org.openapitools.client.ApiException;
import org.openapitools.client.Configuration;
import org.openapitools.client.model.*;
import org.openapitools.client.api.DefaultApi;

public class App {
    public static void main(String[] args) {
        ApiClient defaultClient = Configuration.getDefaultApiClient();

        DefaultApi apiInstance = new DefaultApi(defaultClient);
        var extractResumeRequest = new ExtractResumeRequest();
        extractResumeRequest.setResume("""
        John Doe

        Education
        - University of California, Berkeley
        - B.S. in Computer Science
        - 2020

        Skills
        - Python
        - Java
        - C++
        """);

        try {
            Resume result = apiInstance.extractResume(extractResumeRequest);
            System.out.println(result);
        } catch (ApiException e) {
            System.err.println("Exception when calling DefaultApi#extractResume");
            System.err.println("Status code: " + e.getCode());
            System.err.println("Reason: " + e.getResponseBody());
            System.err.println("Response headers: " + e.getResponseHeaders());
            e.printStackTrace();
        }
    }
}
